mapper.insert:
INSERT INTO ALRAM (
    ALRAM_CODE
    , MEM_ID
    , A_CONTENT
    , SECTION
) VALUES (
    (SELECT 'ALRAM_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ALRAM_CODE, 7))), 0) + 1,3 , '0') 
			FROM ALRAM)
    , #{memId}
    , #{aContent}
    , #{section}
)

mapper.update:
UPDATE ALRAM
SET 
	A_CHECK = #{aCheck}
WHERE ALRAM_CODE = #{alramCode}
