mapper.insert:
INSERT INTO GOODS_BUY (
	BUY_CODE
	, MEM_ID
	, BUY_PRICE
) VALUES (
	#{buyCode}
	, #{memId}
	, #{buyPrice}
)

INSERT INTO GOODS_BUY_DETAIL (
	BUY_DETAIL_CODE
	, GOODS_CODE
	, BUY_DETAIL_CNT
	, BUY_DETAIL_PRICE
	, BUY_CODE
)

	SELECT(SELECT 'BUY_DETAIL_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BUY_DETAIL_CODE, 12))), 0) + 1 + #{i}, 3, '0') 
			FROM GOODS_BUY_DETAIL)
		, #{buyDetail.goodsCode}
		, #{buyDetail.buyDetailCnt}
		, #{buyDetail.buyDetailPrice}
		, #{buyDetail.buyCode}
	FROM DUAL

INSERT INTO ORDER_STATUS (
	ORDER_NO
	, STATUS_CODE
	, BUY_CODE
	, MEM_ID
) VALUES (
	(SELECT NVL(MAX(ORDER_NO), 0) + 1 FROM ORDER_STATUS)
	, 1
	, #{buyCode}
	, #{memId}
)
