mapper.insert:
INSERT INTO GOODS_BUY (
	BUY_CODE
	, MEM_ID
	, BUY_PRICE
	, ORDER_NO
) VALUES (
	(SELECT 'BUY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BUY_CODE, 5))), 0) + 1, 3, '0') 
		FROM GOODS_BUY)
	, #{memId}
	, #{buyPrice}
	,1
)

INSERT INTO GOODS_BUY_DETAIL (
	BUY_DETAIL_CODE
