	SELECT(SELECT 'BUY_DETAIL_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BUY_DETAIL_CODE, 12))), 0) + 1 + #{i}, 3, '0') 
			FROM GOODS_BUY_DETAIL)
		, #{buyDetail.goodsCode}
