mapper.select:
SELECT CLUB_CODE
FROM BOOK_CLUB
WHERE MEM_ID = #{memId}

SELECT COUNT(MEM_ID)
FROM BOOK_CLUB
WHERE MEM_ID = #{memId}

SELECT COUNT(CLUB_NAME)
FROM BOOK_CLUB
WHERE CLUB_NAME = #{clubName}

mapper.insert:
INSERT INTO BOOK_CLUB (
	CLUB_CODE
	, CLUB_NAME
	, CLUB_SHORT_INFO
	, CLUB_INTRO
	, CLUB_MEM_CNT
	, MEM_ID
) VALUES (
	#{clubCode}
	, #{clubName}
	, #{clubShortInfo}
	, #{clubIntro}
	, #{clubMemCnt}
	, #{memId}
)

mapper.update:
UPDATE BOOK_CLUB_MEMBER
SET
	CLUB_MEM_STATUS = #{clubMemStatus}
WHERE CLUB_CODE = #{clubCode}
AND MEM_ID = #{memId}

UPDATE BOOK_CLUB_MEMBER
SET
	CLUB_ROLE = #{clubRole}
WHERE CLUB_CODE = #{clubCode}
AND MEM_ID = #{memId}

mapper.insert:
INSERT INTO BOOK_CLUB_IMAGE (
	CLUB_IMG_CODE
	, CLUB_CODE
	, BC_ORIGIN_FILE_NAME
	, BC_ATTACHED_FILE_NAME
) VALUES (
	(SELECT 'CLUB_IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CLUB_IMG_CODE, 10))), 0) + 1,3 , '0') 
	FROM BOOK_CLUB_IMAGE)
	, #{clubCode}
	, #{bcOriginFileName}
	, #{bcAttachedFileName}
)

INSERT INTO COMMUNITY_IMAGE (
	COM_IMG_CODE
	, BOARD_NUM
	, BC_ORIGIN_FILE_NAME
	, BC_ATTACHED_FILE_NAME
) VALUES (
	(SELECT 'COM_IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(COM_IMG_CODE, 9))), 0) + 1,3 , '0') 
	FROM COMMUNITY_IMAGE)
	, #{boardNum}
	, #{bcOriginFileName}
	, #{bcAttachedFileName}
)

mapper.select:
SELECT COUNT(MEM_ID)
FROM BOOK_CLUB_MEMBER
