mapper.select:
SELECT BC_ATTACHED_FILE_NAME
FROM BOOK_CLUB_IMAGE
WHERE CLUB_CODE = #{clubCode}

SELECT BC_ATTACHED_FILE_NAME
FROM COMMUNITY_IMAGE
WHERE BOARD_NUM = #{boardNum}

mapper.update:
UPDATE BOOK_CLUB
SET
	CLUB_SHORT_INFO = #{clubShortInfo}
	, CLUB_INTRO = #{clubIntro}
WHERE CLUB_CODE = #{clubCode}

mapper.select:
SELECT (SELECT COUNT(BOARD_NUM) FROM COMMUNITY_BOARD WHERE CLUB_CODE = #{clubCode}) - ROW_NUM + 1 AS ROW_NUMBER , BOARD_NUM , BOARD_TITLE , BOARD_WRITER , MEM_NAME , REG_DATE , READ_CNT , ROW_NUM , REPLY_CNT , CLUB_CODE , CLUB_NOTICE FROM ( SELECT BOARD_NUM , BOARD_TITLE , BOARD_WRITER , MEM_NAME , REG_DATE , READ_CNT , ROWNUM ROW_NUM , REPLY_CNT , CLUB_CODE , CLUB_NOTICE FROM ( SELECT BOARD_NUM , BOARD_TITLE , BOARD_WRITER , MEM_NAME , TO_CHAR(REG_DATE, 'YYYY-MM-DD') AS REG_DATE , READ_CNT , (SELECT COUNT(REPLY_NUM) FROM COMMUNITY_REPLY WHERE BOARD_NUM = CB.BOARD_NUM) AS REPLY_CNT , CLUB_CODE , CLUB_NOTICE FROM COMMUNITY_BOARD CB, MEMBER M WHERE BOARD_WRITER = MEM_ID AND CLUB_CODE = #{clubCode}
AND LOWER(BOARD_TITLE) LIKE '%' || LOWER(#{searchText}) || '%'
AND LOWER(BOARD_WRITER) LIKE '%' || LOWER(#{searchText}) || '%'
ORDER BY BOARD_NUM DESC ) ) WHERE ROW_NUM >= #{startDataNum} AND ROW_NUM <= #{endDataNum}

mapper.insert:
INSERT INTO COMMUNITY_BOARD (
	BOARD_NUM
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_WRITER
	, CLUB_CODE

	, CLUB_NOTICE

) VALUES (
	#{boardNum}
	, #{boardTitle}
	, #{boardContent}
	, #{boardWriter}
	, #{clubCode}

	, #{clubNotice}

)

mapper.update:
UPDATE COMMUNITY_BOARD
SET
	BOARD_TITLE = #{boardTitle}
	, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUM = #{boardNum}

mapper.insert:
INSERT INTO COMMUNITY_REPLY (
	REPLY_NUM
	, REPLY_CONTENT
	, REPLY_WRITER
	, BOARD_NUM
) VALUES (
	(SELECT 'REPLY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(REPLY_NUM, 7))), 0) + 1,3 , '0') 
	FROM COMMUNITY_REPLY)
	, #{replyContent}
	, #{replyWriter}
	, #{boardNum}
)

mapper.update:
UPDATE COMMUNITY_REPLY
SET
	REPLY_CONTENT = #{replyContent}
WHERE REPLY_NUM = #{replyNum}

