mapper.select:
SELECT COUNT(CLUB_NAME)
FROM BOOK_CLUB
WHERE CLUB_NAME = #{clubName}

mapper.insert:
INSERT INTO BOOK_CLUB (
	CLUB_CODE
	, CLUB_NAME
	, CLUB_INTRO
	, MEM_ID
) VALUES (
	#{clubCode}
	, #{clubName}
	, #{clubIntro}
	, #{memId}
)

INSERT INTO BOOK_CLUB_IMAGE (
	CLUB_IMG_CODE
	, CLUB_CODE
	, BC_ORIGIN_FILE_NAME
	, BC_ATTACHED_FILE_NAME
) VALUES (
	(SELECT 'CLUB_IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CLUB_IMG_CODE, 10))), 0) + 1,3 , '0') 
	FROM BOOK_CLUB_IMAGE)
	, #{clubCode}
	, #{bcOriginFileName}
	, #{bcAttachedFileName}
)

INSERT INTO BOOK_CLUB_MEMBER (
	CLUB_CODE
	, MEM_ID
) VALUES (
	#{clubCode}
	, #{memId}
)
