mapper.select:
SELECT MEM_ID
FROM MEMBER
WHERE MEM_ID = #{memId}

mapper.insert:
INSERT INTO MEMBER (
	MEM_ID
	, MEM_PW
	, MEM_NAME
	, MEM_EMAIL
	, MEM_TELL

	, MEM_BIRTHDAY


	, MEM_GENDER

	, MEM_ADDR
	, MEM_ADDR_DETAIL
) VALUES (
	#{memId}
	, #{memPw}
	, #{memName}
	, #{memEmail}
	, #{memTell}

	, #{memBirthday}


	, #{memGender}

	, #{memAddr}
	, #{memAddrDetail}
)

mapper.select:
SELECT COUNT(MEM_ID)
FROM MEMBER
WHERE ${column} = #{value}
	AND MEM_ID = #{memId}
	AND MEM_STATUS != '탈퇴'

mapper.update:
UPDATE MEMBER
SET MEM_PW = #{memPw}
WHERE MEM_ID = #{memId}

