mapper.insert:
INSERT INTO NOTICE (
	NOTICE_NO
	, NOTICE_TITLE
	, NOTICE_CONTENT
	, MEM_ID
) VALUES (
	 (SELECT 'NOTICE_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(NOTICE_NO, 8))), 0) + 1, 3, '0') AS NOTICE_NO
FROM NOTICE)
	, #{noticeTitle}
	, #{noticeContent}
	, #{memId}
)

mapper.update:
UPDATE NOTICE
SET NOTICE_CONTENT = #{noticeContent}
WHERE NOTICE_NO = #{noticeNo}
