mapper.insert:
INSERT INTO QNA (
	QNA_NUM
	, QNA_CODE
	, QNA_TITLE
	, QNA_CONTENT
	, MEM_ID		
	, QNA_VIEW_CNT

) VALUES (
	(SELECT NVL(MAX(ROW_NUM), 0) + 1 FROM QNA)
	, (SELECT 'QNA_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(QNA_CODE, 5))), 0) + 1, 3, '0') AS QNA_CODE
	FROM QNA)
	, #{qnaTitle}
	, #{qnaContent}
	, #{memId}
	, #{qnaViewCnt}
)

mapper.update:
UPDATE QNA
SET QNA_VIEW_CNT = QNA_VIEW_CNT + 1
WHERE QNA_CODE = #{qnaCode}

mapper.select:
SELECT (SELECT COUNT(QNA_NUM) FROM QNA) - ROW_NUM + 1 AS ROW_NUMBER
, QNA_NUM , QNA_TITLE , QNA_CODE , MEM_ID , QNA_CONTENT , TO_CHAR(QNA_CREATE_DATE , 'YY-MM-DD HH24:MI') AS QNA_CREATE_DATE , QNA_VIEW_CNT , ROW_NUM , IS_ADMIN_ANSWER
FROM ( SELECT QNA_NUM , QNA_CODE , QNA_TITLE , MEM_ID , QNA_CONTENT , QNA_CREATE_DATE , QNA_VIEW_CNT , ROWNUM ROW_NUM , INNER_ROW , IS_ADMIN_ANSWER FROM ( SELECT QNA_NUM , QNA_CODE , QNA_TITLE , MEM_ID , QNA_CONTENT , QNA_CREATE_DATE , QNA_VIEW_CNT , ROWNUM INNER_ROW , IS_ADMIN_ANSWER FROM QNA ORDER BY QNA_CREATE_DATE DESC ) ) WHERE ROW_NUM >= #{startDataNum} AND ROW_NUM <= #{endDataNum}

mapper.update:
UPDATE QNA
SET QNA_CONTENT = #{qnaContent}
	, QNA_TITLE = #{qnaTitle}
WHERE QNA_CODE = #{qnaCode}

UPDATE QNA
SET IS_ADMIN_ANSWER = 'Y'
WHERE QNA_CODE = #{qnaCode}
