mapper.insert:
		INSERT INTO QNA_ANSWER (
			ANSWER_CODE
			, ANSWER_CONTENT
			, MEM_ID
			, QNA_CODE
			, IS_ADMIN_ANSWER
		) VALUES (
		 (SELECT 'ANSWER_' || LPAD(NVL(MAX(TO_NUMBER
        (SUBSTR(ANSWER_CODE, 8))), 0) + 1, 3, '0') 
        AS ANSWER_CODE FROM QNA_ANSWER)
			, #{answerContent}
			, #{memId}
			, #{qnaCode}
			, 'Y'
		)

mapper.update:
UPDATE QNA_ANSWER
SET ANSWER_CONTENT = #{answerContent}
WHERE ANSWER_CODE = #{answerCode}
