mapper.insert:
INSERT INTO BOOK_RECORD (
	RECORD_CODE
	, MEM_ID
	, BOOK_CODE
	, START_DATE
	, END_DATE
	, BOOK_REVIEW
) VALUES (
	(SELECT 'BK_RECORD_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(RECORD_CODE, 11))), 0) + 1, 3, '0') FROM BOOK_RECORD)
	, #{memId}
	, #{bookCode}
	, #{startDate}
	, #{endDate}
	, #{bookReview}
)

mapper.select:
SELECT RECORD_CODE , BR.BOOK_CODE , TO_CHAR(START_DATE, 'YYYY-MM-DD') START_DATE , TO_CHAR(END_DATE, 'YYYY-MM-DD') END_DATE , BOOK_REVIEW , BOOK_TITLE , BOOK_AUTHOR , ATTACHED_FILE_NAME FROM BOOK_RECORD BR , BOOK BK , BOOK_IMAGE BI WHERE BR.BOOK_CODE = BK.BOOK_CODE AND BK.BOOK_CODE = BI.BOOK_CODE AND IS_MAIN_IMG = 'Y' AND MEM_ID = #{searchMemId}
AND ${searchColumn} LIKE '%'||#{searchValue}||'%'
ORDER BY REG_DATE ${searchOrder}

mapper.update:
UPDATE BOOK_RECORD
SET BOOK_REVIEW = #{bookReview}
	, START_DATE = #{startDate}
	, END_DATE = #{endDate}
WHERE RECORD_CODE = #{recordCode}
