mapper.insert:
		INSERT INTO SEAT_USE (
			SEAT_USE_CODE
            , SEAT_CODE
            , MEM_ID
		) VALUES (
			(SELECT 'SEAT_USE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(SEAT_USE_CODE, 10))), 0) + 1,3 , '0') 
			FROM SEAT_USE)
            , #{seatCode}
            , #{memId}
		)

mapper.update:
UPDATE SECTION_SEAT
SET SEAT_IS_USED = 'Y'
WHERE SEAT_CODE = #{seatCode}

UPDATE SEAT_USE
SET SEAT_OUT_DATE = SYSDATE
WHERE SEAT_USE_CODE = #{seatUseCode}

UPDATE SECTION_SEAT
SET SEAT_IS_USED = 'N'
WHERE SEAT_CODE = #{seatCode}

mapper.select:
SELECT SEAT_USE_CODE , SEAT_CODE , MEM_ID , TO_CHAR(SEAT_IN_DATE, 'YYYY-MM-DD HH24:MI') AS SEAT_IN_DATE , TO_CHAR(SEAT_OUT_DATE, 'YYYY-MM-DD HH24:MI') AS SEAT_OUT_DATE FROM SEAT_USE
AND TO_CHAR(SEAT_IN_DATE, 'YYYY-MM-DD HH24:MI') >= #{searchFromDate}
AND TO_CHAR(SEAT_OUT_DATE, 'YYYY-MM-DD HH24:MI') <= #{searchToDate}
AND UPPER(MEM_ID) LIKE '%'||UPPER(#{seatListSearchVO.searchId})||'%'
ORDER BY SEAT_USE_CODE DESC
